''' given an array of integers, compute all possible results that can be generated by
inserting mathematical operations (+ , - , * , / ) in between
ie: [2,2] -> 4, 0, 4, 1 -> 0, 1, 4'''

def iter(arr):

	res = set([arr[0]])	

	for y in arr[1:]:
		newRes = set()		
		for x in res:
			newRes.add(x+y)
			newRes.add(x-y)
			newRes.add(x*y)
			newRes.add(x//y)
		res = newRes

	return(sorted(res))

print(iter([1,2,3]))

def rec(arr):

	def doMaths(x,y):
		z = set()
		z.add(x+y)
		z.add(x-y)
		z.add(x*y)
		z.add(x//y)
		return z

	def helper(arr,res):
		# print(arr,res)
		if len(arr) == 0:
			return sorted(res)
		else:
			newRes = set()
			y = arr[0]
			for x in res:
				newRes = newRes.union(doMaths(x,y))

			return helper(arr[1:],newRes)

	return helper(arr[1:], set([arr[0]]))

print(rec([1,2,3]))

